import React, { useMemo } from 'react'
import { Chart as ChartJs, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, ArcElement } from 'chart.js'
import { Line } from 'react-chartjs-2'
import styled from 'styled-components'
import { useGlobalContext } from '../../context/globalContext'
import { dateFormat } from '../../utils/dateFormat'

ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
)

function Chart() {
    const { incomes, expenses } = useGlobalContext()

    const combinedData = useMemo(() => {
        if (incomes.length === 0 || expenses.length === 0 || incomes.length !== expenses.length) {
            return []
        }

        return incomes.map((income, index) => {
            const incomeAmount = income.amount
            const expenseAmount = expenses[index]?.amount || 0
            return incomeAmount - expenseAmount
        })
    }, [incomes, expenses])

    const data = {
        labels: incomes.map((inc) => dateFormat(inc.date)),
        datasets: [
            {
                label: 'Net Income',
                data: combinedData,
                backgroundColor: 'green',
                tension: .2,
                borderColor: 'green',
                borderWidth: 2,
                pointBorderColor: 'green',
                pointBackgroundColor: '#fff',
                pointRadius: 4,
                pointHoverRadius: 6,
                pointHitRadius: 20,
                pointHoverBackgroundColor: 'rgba(75,192,192,1)',
                pointHoverBorderColor: 'rgba(220,220,220,1)',
                pointHoverBorderWidth: 2,
                pointStyle: 'circle',
            }
        ]
    }

    if (incomes.length === 0 && expenses.length === 0) {
        return <NoData>No data available</NoData>
    }

    return (
        <ChartStyled>
            <Line data={data} />
        </ChartStyled>
    )
}

const ChartStyled = styled.div`
    background: #FCF6F9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
`;

const NoData = styled.div`
    text-align: center;
    padding: 1rem;
    font-size: 1.2rem;
    color: #777;
`;

export default Chart
